<?php/** * The default template for displaying content * * Used for both single and index/archive/search. * * @package WordPress */$g5plus_archive_loop = g5plus_archive_loop();$g5plus_options = g5plus_option();if ( isset( $g5plus_archive_loop['image-size'] ) ) {	$size = $g5plus_archive_loop['image-size'];} else {	$size = 'full';}$archive_style = 'classic';if ( isset( $g5plus_archive_loop['style'] ) && !empty( $g5plus_archive_loop['style'] ) ) {	$archive_style = $g5plus_archive_loop['style'];}$class   = array();$class[] = "clearfix";if ( get_post_format() == 'audio' ) {	$min_suffix = ( isset( $g5plus_options['enable_minifile_css'] ) && $g5plus_options['enable_minifile_css'] == 1 ) ? '.min' : '';	wp_enqueue_script( 'g5plus-jplayer-js',  get_template_directory_uri()  . '/assets/plugins/jquery.jPlayer/jquery.jplayer.min.js', array( 'jquery' ), '', true );	wp_enqueue_style( 'g5plus-jplayer-style' );}?><article id="post-<?php the_ID(); ?>" <?php post_class( $class ); ?>>	<div class="entry-wrap clearfix">		<?php		$thumbnail = g5plus_post_thumbnail( $size );		if ( !empty( $thumbnail ) ) : ?>			<div class="entry-thumbnail-wrap">				<?php echo wp_kses_post( $thumbnail ); ?>			</div>		<?php endif; ?>		<div class="entry-content-wrap">			<div class="entry-content-top-wrap clearfix">				<div class="entry-content-top-right">					<h3 class="entry-title">						<a href="<?php the_permalink(); ?>" rel="bookmark" title="<?php the_title(); ?>"><?php the_title(); ?></a>					</h3>					<div class="entry-post-meta-wrap">						<?php g5plus_post_meta(); ?>					</div>				</div>			</div>			<div class="entry-excerpt">				<?php the_excerpt(); ?>			</div>			<div class="entry-post-meta-footer">				<?php g5plus_post_meta_footer(); ?>			</div>			<!--            <a class="cortana-button style4 size-sm" href="--><?php //the_permalink(); ?><!--" rel="bookmark" title="--><?php //the_title(); ?><!--">--><?php //_e('Read more','cortana') ?><!--</a>-->		</div>	</div></article>