<?php/** * Created by PhpStorm. * User: PhanLong * Date: 10/19/2015 * Time: 3:26 PM */?><ul class="entry-footer clearfix">	<?php	if ( !is_single() ) {		?>		<li class="entry-meta-count-post">			<a href="<?php the_permalink() ?>">				<i class="icon icon-heart"></i>				<?php echo g5plus_get_post_views( get_the_ID() ); ?>				<?php if ( g5plus_get_post_views( get_the_ID() ) != '0' ) {					echo esc_html__( 'Views', 'cortana' );				} else {					echo esc_html__( 'View', 'cortana' );				} ?>			</a>		</li>		<?php if ( comments_open() || get_comments_number() ) : ?>			<li class="entry-meta-comment">				<?php $allowed_html = array(					'i'  => array(						'class' => array(),					),					'br' => array(),					'em' => array(),				); ?>				<?php comments_popup_link( wp_kses( __( '<i class="icon icon-chat"></i> 0 Comment', 'cortana' ), $allowed_html ), wp_kses( __( '<i class="icon icon-chat"></i> 1 Comment', 'cortana' ), $allowed_html ), wp_kses( __( '<i class="icon icon-chat"></i> % Comments', 'cortana' ), $allowed_html ) ); ?>			</li>		<?php endif; ?>		<?php	}	?>	<?php	if ( is_single() ) {		the_tags( '<li class="entry-meta-tags"><span><i class="fa fa-tags"></i> Tags: </span>', ',', '</li>' );	}	?>	<li class="entry-meta-share">		<?php g5plus_share(); ?>	</li></ul>